package javaDemo;

class details{
	String name;
	int age;
}

public class InstanceVariable {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		details d = new details();
		d.name = "Thrishala";
		d.age = 23;
		System.out.println(d.name);
		System.out.println(d.age);
	}
}


